import useSWR from "swr";
import { useState } from "react";
import swrConfig from "../util/swrConfig";

export default function Database() {
  let dbData = {};
  if (!dbData) {
    const { data, error } = useSWR(
      "/api/db",
      url => fetch(url).then(r => r.json()),
      swrConfig
    );
    if (error) throw error;
    dbData = data;
  }
  return (
    <>
      <h2>Content: {(dbData && dbData.content) || "Loading..."}</h2>
      <h2>Author: {(dbData && dbData.author) || "Loading..."}</h2>
      <h2>Date: {(dbData && dbData.date) || "Loading..."}</h2>
      <h1>db details</h1>
    </>
  );
}
