import { MongoClient } from "mongodb";
import dbConfig from "../../util/dbConfig";
//TODO: vezi de ce se tot conecteaza dijaba la server
export default (req, res) => {
  MongoClient.connect(dbConfig.dbURI, async (error, client) => {
    if (error) throw error;
    try {
      const db = client.db(dbConfig.dbName);
      console.log("connected to the server!");
      const queryRes = db
        .collection("quotes")
        .findOne({ author: "MWR" })
        .then(console.log)
        .catch(console.error);
      console.log(queryRes);
      res
        .status(200)
        .json(await db.collection("quotes").findOne({ author: "MWR" }));

      client
        .close()
        .then(() => console.log("connection closed."))
        .catch(console.error);
    } catch (err) {
      return console.error(err);
    }
  });
};
