import { useState } from "react";

function useInputManagement() {
  const [input, setInput] = useState({
    name: "",
    email: ""
  });

  const onChange = (e, inputType) =>
    setInput({ ...input, [inputType]: e.currentTarget.value });

  return [input, onChange];
}

export default function About() {
  const [input, onChange] = useInputManagement();

  function onSubmit(e) {
    e.preventDefault();
    console.log(input);
  }
  const inputs = ["name", "email"];
  return (
    <>
      <form onSubmit={onSubmit}>
        {inputs.map((inputType, i) => (
          <>
            <input
              key={`input_${i}`}
              type="text"
              placeholder={inputType}
              onChange={e => onChange(e, inputType)}
            />
            <br />
          </>
        ))}
        <button type="submit">Submit</button>
      </form>
    </>
  );
}
