import { MongoClient } from "mongodb";
import { dbURI } from "../../util/dbConfig";

export default (req, res) => {
  console.log(
    MongoClient.connect(dbURI, (err, client) => {
      if (err) throw err;
      const db = client.collection("quotes");
    })
  );
  res.status(404);
};
