import { useState } from "react";

export default function About() {
  const [input, setInput] = useState("");

  function onSubmit(e) {
    e.preventDefault();
    console.log(input);
  }

  return (
    <>
      <form onSubmit={onSubmit}>
        <input
          placeholder="author"
          onChange={e => setInput(e.currentTarget.value)}
        />
        <button type="submit">Submit</button>
      </form>
    </>
  );
}
