import useSWR from "swr";
import swrConfig from "../util/swrConfig";

let accessedState = false;

export default function Database() {
  if (!accessedState) {
    const { data, error } = useSWR(
      "/api/db",
      url =>
        fetch(url)
          .then(r => r.json())
          .then(r => {
            return r;
          }),
      swrConfig
    );
    if (error) throw error;
    accessedState = true;
  }

  return (
    <>
      <h2>Content: {(data && data.content) || "Loading..."}</h2>
      <h2>Author: {(data && data.author) || "Loading..."}</h2>
      <h2>Date: {(data && data.date) || "Loading..."}</h2>
      <h1>db details</h1>
    </>
  );
}
