import { MongoClient } from "mongodb";
import { dbName, dbURI } from "../../util/dbConfig";

export default (req, res) => {
  MongoClient.connect(dbURI, async (error, client) => {
    if (error) throw error;

    let queryResult = "";
    const db = client.db(dbName);
    console.log("connected to the server!");

    queryResult = await db.collection("quotes").findOne({ author: "MWR" });

    client.close();
  });
  res.status(200).json(queryResult);
};
