import useInputManagement from "../components/hooks/inputManagement";
import Router from "next/router";
export default function About() {
  const [inputData, setInputEvent] = useInputManagement({
    author: ""
  });

  function onSubmit(e) {
    e.preventDefault();
    fetch("/api/db", {
      method: "POST",
      headers: {
        "Content-type": "application/json"
      },
      body: JSON.stringify(inputData)
    }).then(() => Router.push("/"));
  }

  const inputs = ["author"];
  return (
    <>
      <form onSubmit={onSubmit}>
        {inputs.map((inputType, i) => (
          <>
            <input
              key={`input_${i}`}
              type="text"
              placeholder={inputType}
              onChange={e => setInputEvent(e, inputType)}
            />
            <br />
          </>
        ))}
        <button type="submit">Submit</button>
      </form>
    </>
  );
}
