import checkFieldsValidity from "../../utils/checkFieldsValidity";
export default function submitHandler({
    e,
    userDetails,
    showErrorMessage,
    setDisabled,
    modalHandler,
    modifyAlert,
    fetchEndpoint,
}) {
    e.preventDefault();
    setDisabled(true);

    const errorMessage = checkFieldsValidity({
        fields: userDetails,
        minimumLengthForEachField: {
            name: 3,
            surname: 3,
            username: 5,
            password: 8,
        },
        hasEmail: true,
    });

    if (errorMessage !== false) {
        setDisabled(false);
        return showErrorMessage(errorMessage);
    }

    fetch(fetchEndpoint, {
        method: "POST",
        body: JSON.stringify(userDetails),
    })
        .then((r) => r.json())
        .then(({ ok, error = null }) => {
            if (!ok)
                modifyAlert({
                    isVisible: true,
                    props: {
                        type: 0,
                        children: !error
                            ? "A apărut o eroare internă, vă rugam să ne scuzați."
                            : error,
                    },
                });
            else
                modifyAlert({
                    isVisible: true,
                    props: {
                        type: 1,
                        children: "Operațiunea a fost efectuată cu succes.",
                    },
                });

            modalHandler();
        })
        .catch((error) => {
            console.error(error);
            setDisabled(false);
            showErrorMessage("A aparut o eroare interna, va rog sa ne scuzati.");
        });
}
