import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import CheckmarkSolved from "../utils/CheckmarkSolved";
import OverlayDarkener from "../utils/OverlayDarkener";
export default function AlertNotification({
    type,
    children,
    alertToggleHandler,
}) {
    return (
        <>
            <OverlayDarkener onClick={alertToggleHandler} />
            <div className="alert">
                <div className="alert__info">
                    {type === 1 ? (
                        <>
                            <h2>Succes!</h2>
                            <CheckmarkSolved />
                        </>
                    ) : (
                        <>
                            <h2>Eroare!</h2>
                            <FontAwesomeIcon icon="times-circle" />
                        </>
                    )}
                </div>
                <p>{children}</p>
                <button
                    className="alert__button button--tertiary"
                    onClick={alertToggleHandler}
                >
                    OK
                </button>

                <style jsx>{`
                    .alert {
                        max-width: 50%;
                        height: 30%;
                        padding: 30px 30px 60px;
                        background-color: var(--background-primary);
                        border-radius: 20px;
                        position: fixed;
                        margin: auto;
                        top: 0;
                        left: 0;
                        right: 0;
                        bottom: 0;
                        z-index: 100;
                        color: var(--text-primary);
                        box-shadow: var(--box-shadow);
                        opacity: 0;
                        transform: scale(0.5);
                        animation: comeIn 500ms ease forwards;
                    }

                    h2 {
                        display: inline-block;
                        vertical-align: middle;
                    }

                    p {
                        margin-top: 10px;
                    }

                    .alert__button {
                        position: absolute;
                        right: 0;
                    }

                    @keyframes comeIn {
                        from {
                            transform: scale(0.5);
                            opacity: 0;
                        }
                        80% {
                            transform: scale(1.1);
                            opacity: 0.8;
                        }
                        to {
                            transform: scale(1);
                            opacity: 1;
                        }
                    }
                `}</style>
            </div>
        </>
    );
}
