import { useState } from "react";
import { useRouter } from "next/router";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

import InputArea from "../utils/InputArea";
import formModal from "../../styles/formModal";
import OverlayDarkener from "../utils/OverlayDarkener";

import checkFieldsValidity from "../../utils/checkFieldsValidity";
import abortableFetch from "../../utils/abortableFetch";

export default function Register({ showRegisterModalHandler }) {
    const [userDetails, setUserDetail] = useState({
        name: "",
        surname: "",
        county: "Alba",
        profession: "Elev",
        username: "",
        email: "",
        password: "",
    });
    const userDetailChanger = ({ target }, userDetail) =>
        setUserDetail({ ...userDetails, [userDetail]: target.value });

    const [errorMessage, setErrorMessage] = useState("");
    const showErrorMessage = (error) => setErrorMessage(error);

    const inputAreaData = [
        {
            title: "Nume",
            inputType: "text",
            inputProps: { minLength: 3, required: true },
            eventHandler: (e) => userDetailChanger(e, "name"),
        },
        {
            title: "Prenume",
            inputType: "text",
            inputProps: { minLength: 3, required: true },
            eventHandler: (e) => userDetailChanger(e, "surname"),
        },
        {
            title: "JudeÈ›",
            isSelect: true,
            optionValues: ["Alba", "Cluj", "Bistrita-Nasaud"],
            eventHandler: (e) => userDetailChanger(e, "county"),
        },
        {
            title: "Profesie",
            isSelect: true,
            optionValues: ["Elev", "Profesor"],
            eventHandler: (e) => userDetailChanger(e, "profession"),
        },
        {
            title: "Nume Utilizator",
            inputType: "text",
            inputProps: { minLength: 5, required: true },
            eventHandler: (e) => userDetailChanger(e, "username"),
        },
        {
            title: "E-mail",
            inputType: "email",
            inputProps: {
                required: true,
            },
            eventHandler: (e) => userDetailChanger(e, "email"),
        },
        {
            title: "Parola",
            inputType: "password",
            inputProps: { minLength: 8, required: true },
            eventHandler: (e) => userDetailChanger(e, "password"),
        },
    ];

    return (
        <>
            <OverlayDarkener
                onClick={() => {
                    showRegisterModalHandler();
                }}
            />
            <div className="modal">
                <div className="modal__title-container">
                    <RegisterModalWave />
                    <h2 className="modal__title">ÃŽnregistreazÄƒ-te</h2>
                </div>
                <form className="modal__input-panels" onSubmit={() => false}>
                    <div className="modal__input-panel-group">
                        {inputAreaData.map((props) => (
                            <div className="modal__input-panel">
                                <InputArea {...props} />
                            </div>
                        ))}
                    </div>

                    <div className="modal__buttons-container">
                        <button
                            className="button--tertiary"
                            onClick={showRegisterModalHandler}
                        >
                            RenunÈ›Äƒ
                        </button>
                        <SubmitButton
                            userDetails={userDetails}
                            showErrorMessage={showErrorMessage}
                        />
                    </div>
                    {errorMessage !== "" && (
                        <p className="error-message">
                            {errorMessage}{" "}
                            <FontAwesomeIcon icon="times-circle" />{" "}
                        </p>
                    )}
                </form>
            </div>
            <style jsx>{formModal}</style>
            <style jsx>{`
                .modal {
                    width: 50%;
                }

                .modal__input-panel:nth-child(odd) {
                    margin-right: 15px;
                }

                .modal__input-panel:nth-child(even) {
                    margin-left: 15px;
                }

                .modal__input-panel.modal__input-panel--last {
                    margin-right: 0;
                    margin-left: 0;
                }
                @media screen and (max-width: 825px) {
                    .modal__input-panel:nth-child(n) {
                        margin-left: 0;
                        margin-right: 0;
                    }
                }
            `}</style>
        </>
    );
}

function SubmitButton({ userDetails, showErrorMessage }) {
    const [isDisabled, setDisabled] = useState(false);
    const Router = useRouter();

    return (
        <>
            <button
                type="submit"
                disabled={isDisabled}
                onClick={() => registerSubmitHandler()}
                className="button--primary"
            >
                CreeazÄƒ
            </button>
            <style jsx>{`
                .button--primary {
                    background-color: var(--accent-secondary);
                }
            `}</style>
        </>
    );
}

function RegisterModalWave() {
    return (
        <svg
            viewBox="0 0 646 184"
            fill="none"
            xmlns="http://www.w3.org/2000/svg"
            className="modal__wave"
            preserveAspectRatio="none"
        >
            <path
                d="M0 0L646 9.53674e-07V155.911C646 155.911 580.59 179.393 468.2 182.62C355.81 185.847 285.267 171.488 207.13 155.911C46.027 123.793 1.90735e-06 155.911 1.90735e-06 155.911L0 0Z"
                fill="var(--accent-secondary)"
            />
        </svg>
    );
}

function registerSubmitHandler() {
    e.preventDefault();
    setDisabled(true);

    const errorMessage = checkFieldsValidity({
        fields: userDetails,
        minimumLengthForEachField: {
            name: 3,
            surname: 3,
            username: 5,
            password: 8,
        },
        hasEmail: true,
    });

    if (errorMessage !== false) {
        setDisabled(false);
        return showErrorMessage(errorMessage);
    }

    const { data, abort } = abortableFetch("/api/register", {
        method: "POST",
        body: JSON.stringify(userDetails),
    });

    abort();
    //TODO: abort on signal from other elements that remove the register modal
    data.then((r) => r.json()).then(({ ok }) => {
        if (ok) Router.push("/cont");
        else {
            setDisabled(false);
            return showErrorMessage(
                "A aparut o eroare interna, va rog sa ne scuzati."
            );
        }
    });
}
