import connectToDatabaseThen from "../../utils/connectToDatabase";
import checkFieldsValidity from "../../utils/checkFieldsValidity";
import escape from "validator/lib/escape";

export default (req, res) => {
    const { error, validatedUserData } = validateUserData(req);
    if (error) return res.status(500).json({ ok: false, error });
    req.body = validatedUserData;
    console.log(req.headers);

    connectToDatabaseThen((db) => {
        const users = db.collection("users");

        users.insertOne(req.body, (error) => {
            if (error) {
                console.error(error);
                return res.status(500).json({ ok: false });
            }

            res.status(200).json({ ok: true });
        });
    });
};

function escapeRegisterData(userData) {
    for (const [key, value] of Object.entries(userData))
        userData[key] = escape(value);

    return userData;
}

function validateUserData(req) {
    const escapedFields = escapeRegisterData(JSON.parse(req.body));
    const errorMessage = checkFieldsValidity({
        fields: escapedFields,
        minimumLengthForEachField: {
            name: 3,
            surname: 3,
            username: 5,
            password: 8,
        },
        hasEmail: true,
    });
    if (errorMessage) return { error: errorMessage, validatedUserData: {} };

    return { error: false, validatedUserData: escapedFields };
}
