import InputArea from "../utils/InputArea";
import inputAreaRegisterData from "../utils/inputAreaRegisterData";

export default function InputAreas({ userDetails, setUserDetail }) {
    return inputAreaRegisterData.map(
        (props, i) =>
            i % 2 === 0 &&
            i + 2 !== inputAreaRegisterData.length && (
                <div className="modal__input-panel-group">
                    <div className="modal__input-panel">
                        <InputArea {...props} />
                    </div>
                    <div
                        className={`modal__input-panel ${
                            i + 2 === inputAreaRegisterData.length &&
                            "mdoal__input-panel--last"
                        }`}
                    >
                        <InputArea {...inputAreaRegisterData[i + 1]} />
                    </div>
                </div>
            )
    );
}

