import MongoClient from "mongodb";
import dbConfig from "../configs/db";

export default function connectToDatabase() {
    return new Promise((resolve, reject) => {
        MongoClient.connect(dbConfig.dbURI, (error, client) => {
            if (error) {
                console.error(error);
                reject(error);
                return client.close();
            }

            resolve({ db: client.db(dbConfig.dbName), closeConnection: client.close() });
        });
    });
}
