export default function checkFieldsValidity({
    fields = {},
    minimumLengthForEachField = {},
    hasEmail = false,
}) {
    const fieldValues = Object.values(fields);

    if (thereAreEmptyFields(fieldValues))
        return "Nu ai completat unul sau mai multe c창mpuri.";
    else if (fieldValuesAreNotLongEnough(fields, minimumLengthForEachField))
        return "Unul sau mai multe c창mpuri nu indeplinesc numarul de caractere minim.";
    else if (hasEmail && !isEmailValid(fields.email))
        return "E-mail-ul pe care l-ai introdus nu este valid.";

    return false;
}

function thereAreEmptyFields(fieldValues) {
    return fieldValues.some((fieldValue) => fieldValue.trim() === "");
}

function fieldValuesAreNotLongEnough(fields, minimumLengthForEachField) {
    for (const field of Object.keys(minimumLengthForEachField)) {
        if (fields[field].length < minimumLengthForEachField[field])
            return true;
    }
    return false;
}
