import jwt from "jsonwebtoken";
import isEmail from "validator/lib/isEmail";
import validateUserData from "../../utils/validateUserData";
import connectToDatabase from "../../utils/connectToDatabase";

export default async (req, res) => {
    if (!req.body) return res.status(204).end();
    const { error, validatedUserData } = validateUserData(req.body, {
        minimumLengthForEachField: {
            username: 5,
            password: 8,
        },
        hasEmail: isEmail(req.body.username),
    });
    if (error) return res.status(403).json({ ok: false, error });

    let closeConnection;
    try {
        const { db, closeConnectionHandler } = await connectToDatabase();
        closeConnection = closeConnectionHandler;
        console.log(validatedUserData);
        const foundUser = db.collection("users").findOne({ password: validatedUserData.password });
        console.log(foundUser);
        if (!foundUser) res.status(403).json({ ok: false, error: "Date incorecte!" });
        else res.status(200).json({ ok: true });

        return closeConnection();
    } catch (e) {
        if (closeConnection) closeConnection();

        console.error(e);
        return res.status(500).json({ ok: false });
    }
};
